import React, { useState, useEffect } from 'react'
import { SafeAreaView, Text, Button, FlatList, TextInput } from 'react-native';
import styles from './Appstyles'    


export default function Archive({navigation}) {  

    const [data, setData] =useState([]);
    const [title, onChangeTitle] =useState('')
    const [genre, onChangeGenre] =useState('')
    const [year, onChangeYear] =useState('')

    useEffect(() => {
        getMovies();
      
      }, []);
    
      const getMovies = async () => {
        try {
          const response = await fetch(`https://codycrudapi.herokuapp.com/api/v1/movies`);
          const json = await response.json();
          setData(json)
          console.log(json)
        }
        catch(error){
          console.error(error)
        }
      };
    
    return(
    <SafeAreaView style={styles.container}>
        <Text>Add a Movie to the Archives</Text>
        <TextInput
            placeholder='title'
            onChangeTitle={onChangeTitle}
            value={title}
        />
        <TextInput
            placeholder='genre'
            onChangeText={onChangeGenre}
            value={genre}
        />
        <TextInput
            placeholder='year'
            onChangeYear={onChangeYear}
            value={year}
        />
            
        <Text style={styles.largeHeading}>Archive</Text>
        <FlatList
        keyExtractor={(item) => item.id}
        style={styles.listContainer}
        data={data}
        renderItem={({item}) => (
          <>
            <Text>{item.title}</Text>
          </>
        )}
    />
        <Button title='Go to the Home Page' onPress={() => navigation.navigate('Home')} />
    </SafeAreaView>
    );
}
