import React, { useState, useEffect } from 'react'
import { SafeAreaView, Switch, StyleSheet, Text, View, Button, TextInput } from 'react-native';
import styles from './Appstyles'    


export default function Movie({navigation, route }) {
    const { film } = route.params;
    const [movies, setMovies] = useState([])
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)

    

    // const API_BASE = process.env.NODE_ENV === 'development' 
    // ? 'https://codycrudapi.herokuapp.com/api/v1' 
    // : process.env.REACT_APP_BASE_URL;

    // let ignore = false;
    // useEffect(() => {

    //   if(!ignore){
    //     getMovie()
    //   }

    //   return () => {
    //     ignore = true;
    //   }
    // }, [])

    // const getMovie = async (res, req) =>{
    //   setLoading(true)
    //   try{
    //     await fetch(`${API_BASE}/movies/${id}`)
    //     .then(res => res.json())
    //     .then(data => {
    //     //   console.log({data})
    //       console.log(data[0].title)
    //     })

    //   } catch (error) {
    //     setError(error.message || "Unexpected error")

    //   } finally {
    //     setLoading(false)
    //   }

    // }

    const deleteMovie = async () => {
        try{
            await fetch(`${API_BASE}/movies/${id}`, {
                method: 'DELETE'
            })
                .then(res => res.json())
                .then(data => {
                    setMovies(data)
                    navigation.navigate("Archive", { replace: true})
                })

        } catch (error) {
            setError(error.message || "Unexpected Error")
        } finally {
            setLoading(false)
        }
    }

    const updateMovie = async () => {
        try{
            await fetch(`${API_BASE}/movies/${id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    title:`${title}`,
                    genre:`${genre}`,
                    released: `${released}`
                  })
            })
                .then(res => res.json())
                .then(data => {
                    console.log({data})
                })

        } catch (error) {
            setError(error.message || "Unexpected Error")
        } finally {
            setLoading(false)
        }
    }

    const handleSubmit = () => {
        updateMovie();
    }


    return(
    <SafeAreaView style={styles.container}>
        <Text style={styles.largeHeading}>{film.title}</Text>
        <Text>{film.genre}</Text>
        <Text>{film.released}</Text>
        <Button title='Go to home' onPress={() => navigation.navigate('Home')} />
        <Button title='Go to details' onPress={() => navigation.navigate('Details')} />
        <Button title='Delete Movie' onPress={() => deleteMovie()} />
        <Text>Edit Movie</Text>
        <TextInput
            placeholder='title'
            name='title'
            // onChangeText={setTitle}
            
        />
        <TextInput
            placeholder='genre'
            name='genre'
            // onChangeText={setGenre}
            
        />
        <TextInput
            placeholder='year'
            name='released'
            // onChangeText={setReleased}
           
        />
        <Button title='Submit' onPress={() => handleSubmit()} />
    </SafeAreaView>
    );
}
