import { StatusBar } from 'expo-status-bar';
import { Text, Switch, SafeAreaView, Button, Platform, Flatlist } from 'react-native';
import React, { useState } from "react"
import ListContainer from './components/ListContainer';
import styles from './Appstyles'
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as Network from 'expo-network'; 

import Archive from './Archive';

function HomeScreen({navigation}) {
  const [ isEnabled, setIsEnabled ] = useState(false);
  const toggleSwitch = () => setIsEnabled(previousState => !previousState);

  Network.getNetworkStateAsync().then(data => {
    console.log({data});
  });

  async function getMovies(){
    try {
      let response = await fetch(`https://codycrudapi.herokuapp.com/api/v1/movies`);
      let responseJSON = await response.json();
      console.log(responseJSON)
      return responseJSON;
    }
    catch(error){
      console.error(error)
    }
  }
  const DATA = getMovies();

  const Movie = ({title}) => (
    <SafeAreaView>
      <Text>{title}</Text>
    </SafeAreaView>
  );

  return (
    <SafeAreaView style={styles.container}>
      <Text style={[styles.largeHeading, styles.headingColor]}>Welcome to Movie Lister!</Text>
      <Text>Looking for a good movie to watch tonight? Check out our Movie Archive for an incredible list!</Text>
      <Button title='Go to our Movie Archive' onPress={() => navigation.navigate('Archive')} />
      {
        Platform.OS === 'ios'
        ? <Text>On Mobile</Text>
        : <Text>Desktop</Text>
      }
      <FlatList
        data={DATA}
        renderMovie={({movie}) => <Movie title={movie.title} />}
        keyExtractor={movie => movie._id}
        style={styles.listContainer}

    />
      <ListContainer />
      <Switch 
        trackColor={{ false: "#767577", true: "#81b0ff" }}
        thumbColor={isEnabled ? "#f5dd4b" : "#f4f3f4" }
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleSwitch}
        value={isEnabled}
      />
      <StatusBar style="auto" />
    </SafeAreaView>
  );

}
const Stack = createNativeStackNavigator();
export default function App() {

  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Home" component={HomeScreen} options={{ title:'Movie Lister'}} />
        <Stack.Screen name="Archive" component={Archive} />
      </Stack.Navigator>

    </NavigationContainer>
  );
}
