import React, { useState, useEffect } from 'react'
import { SafeAreaView, Text, Button, FlatList } from 'react-native';
import styles from './Appstyles'    


export default function Archive({navigation}) {  

    const [data, setData] =useState([]);

    useEffect(() => {
        getMovies();
      
      }, []);
    
      const getMovies = async () => {
        try {
          const response = await fetch(`https://codycrudapi.herokuapp.com/api/v1/movies`);
          const json = await response.json();
          setData(json)
          console.log(json)
        }
        catch(error){
          console.error(error)
        }
      };
    
    return(
    <SafeAreaView style={styles.container}>
        <Text style={styles.largeHeading}>Archive</Text>
        <FlatList
        keyExtractor={(item) => item.id}
        style={styles.listContainer}
        data={data}
        renderItem={({item}) => (
          <>
            <Text>{item.title}</Text>
          </>
        )}
    />
        <Button title='Go to the Home Page' onPress={() => navigation.navigate('Home')} />
    </SafeAreaView>
    );
}
