import React, { useState, useEffect } from 'react'
import { SafeAreaView, Switch, StyleSheet, Text, View, Button, TextInput } from 'react-native';
import styles from './Appstyles'    


export default function Movie({navigation, route }) {
    const { film } = route.params;
    const [title, setTitle] = useState('');
    const [genre, setGenre] = useState('');
    const [released, setReleased] = useState('');
    const [movies, setMovies] = useState([])
    const id = film[0]._id;

    

    const API_BASE = process.env.NODE_ENV === 'development' 
    ? 'https://codycrudapi.herokuapp.com/api/v1' 
    : process.env.REACT_APP_BASE_URL
    let ignore = false;
    useEffect(() => {

      if(!ignore){
        getMovie()
      }

      return () => {
        ignore = true;
      }
    }, [])


    const getMovie = async (res, req) =>{
      
      try{
        await fetch(`${API_BASE}/movies/${id}`)
        .then(res => res.json())
        .then(data => {
          setTitle(data.title)
          setGenre(data.genre)
          setReleased(data.released)
          
        })

      } catch (error) {
        console.log(error.message || "Unexpected error")

      } 

    }

    const getMovies = async (res, req) =>{
      try{
        await fetch(`${API_BASE}/movies`)
        .then(res => res.json())
        .then(data => {
          console.log({data})
          setData(data)
          setMovies(data)
        })

      } catch (error) {
        console.error(error.message || "Unexpected error")
    }
  }

    const deleteMovie = async () => {
        try{
            await fetch(`${API_BASE}/movies/${id}`, {
                method: 'DELETE'
            })
              .then(res => res.json())
              .then(data => {
                  navigation.navigate("Archive", () => getMovies())
              
            })
        } catch (error) {
            console.log(error.message || "Unexpected Error")
        }
    }

    const updateMovie = async () => {
        try{
            await fetch(`${API_BASE}/movies/${id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    title:`${title}`,
                    genre:`${genre}`,
                    released: `${released}`
                  })
                 
            })
            .then(res => res.json())
            .then(data => {
              console.log({data})
          })
                

        } catch (error) {
            console.log(error.message || "Unexpected Error")
        }
    }

    const handleSubmit = () => {
        updateMovie();
        console.log(title, genre, released)
    }


    return(
    <SafeAreaView style={styles.container}>
        <Text style={styles.largeHeading}>
        {title}<br></br>
        {genre}<br></br>
        {released}
        </Text>
        <Button title='Go to home' onPress={() => navigation.navigate('Home')} />
        <Button title='Go to details' onPress={() => navigation.navigate('Details')} />
        <Button title='Delete Movie' onPress={() => deleteMovie()} />
        <Text>Edit Movie</Text>
        <TextInput
            placeholder='title'
            name='title'
            onChangeText={setTitle}
            
        />
        <TextInput
            placeholder='genre'
            name='genre'
            onChangeText={setGenre}
            
        />
        <TextInput
            placeholder='year'
            name='released'
            onChangeText={setReleased}
           
        />
        <Button title='Submit' onPress={() => handleSubmit()} />
    </SafeAreaView>
    );
}
