import React, { useState, useEffect } from 'react'
import { SafeAreaView, Text, Button, FlatList, TextInput, Pressable } from 'react-native';
import styles from './Appstyles'    


export default function Archive({navigation,}) {  

    const [data, setData] =useState([]);
   
    const [current, setCurrent] =useState([]);
    // const [title, onChangeTitle] =useState('')
    // const [genre, onChangeGenre] =useState('')
    // const [relesed, onChangeReleased] =useState('')
    const [movies, setMovies] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)

  const [values, setValues] = useState({
    title:'',
    genre:'',
    released:''
  })

  


  const API_BASE = process.env.NODE_ENV === 'development' 
    ? 'https://codycrudapi.herokuapp.com/api/v1' 
    : process.env.REACT_APP_BASE_URL;

    let ignore = false;
    useEffect(() => {

      if(!ignore){
        getMovies()
      }

      return () => {
        ignore = true;
      }
    }, [])

    const getMovies = async (res, req) =>{
      setLoading(true)
      try{
        await fetch(`${API_BASE}/movies`)
        .then(res => res.json())
        .then(data => {
          console.log({data})
          setData(data)
          setMovies(data)
        })

      } catch (error) {
        setError(error.message || "Unexpected error")

      } finally {
        setLoading(false)
      }

    }

    const createMovie = async () => {
      try{
      await fetch(`${API_BASE}/movies`, {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify(values)
          })
              .then(() => getMovies())

      } catch (error) {
          setError(error.message || "Unexpected Error")
      } finally {
          setLoading(false)
      }
  }

    const handleSubmit = (event) => {
    //   event.preventDefault();
      createMovie();
  }

  const handleInputChange = () => {
   
    setValues({
        title: document.getElementsByName('title').innerText,
        genre: document.getElementsByName('genre').innerText,
        released: document.getElementsByName('year').innerText
    })
}
    

    useEffect(() => {
        getMovies();
      
      }, []);

       const movieSelect = async (id) => {
        const movieId = id;
        const movies = data;
        const current = movies.filter(movie => movie._id === movieId)
        console.log(current)
        navigation.navigate('Movie', {
            film: current
        }
        )};

        // const postMovie = async () => {
        //     fetch(`https://codycrudapi.herokuapp.com/api/v1/movies`, {
        //         method: 'POST',
        //         'Content-Type': 'application/json',
        //         body: JSON.stringify({
        //           title: title,
        //           genre: genre,
        //           year: year
        //         }),
        //       });
        // }
    
    return(
    <SafeAreaView style={styles.container}>
        <Text>Add a Movie to the Archives</Text>
        <TextInput
            placeholder='title'
            name='title'
            onChangeText={handleInputChange(e)}
            
        />
        <TextInput
            placeholder='genre'
            name='genre'
            onChangeText={handleInputChange(e)}
            
        />
        <TextInput
            placeholder='year'
            name='released'
            onChangeText={handleInputChange(e)}
           
        />
        <Button title='Submit' onPress={(event) => handleSubmit(e)} />
            
        <Text style={styles.largeHeading}>Archive</Text>
        <FlatList
        value={(item) => item.title}
        id={(item) => item._id}
        keyExtractor={(item) => item._id}
        style={styles.listContainer}
        data={data}
        renderItem={({item}) => (
          <>
            <Pressable  value={item} id={item._id} onPress={() => movieSelect(item._id)}><Text>{item.title}</Text></Pressable>
          </>
        )}
    />
        <Button title='Go to the Home Page' onPress={() => navigation.navigate('Home')} />
    </SafeAreaView>
    );
}
