import React, { useState, useEffect } from 'react'
import { SafeAreaView, Text, Button, FlatList, TextInput, Pressable } from 'react-native';
import styles from './Appstyles'    


export default function Archive({navigation,}) {  

    const [data, setData] =useState([]);
    const [current, setCurrent] =useState([]);
    const [title, onChangeTitle] =useState('')
    const [genre, onChangeGenre] =useState('')
    const [year, onChangeYear] =useState('')

    useEffect(() => {
        getMovies();
      
      }, []);
    
      const getMovies = async () => {
        try {
          const response = await fetch(`https://codycrudapi.herokuapp.com/api/v1/movies`);
          const json = await response.json();
          setData(json)
          console.log(json)
        }
        catch(error){
          console.error(error)
        }
      };

       const movieSelect = async (id) => {
        const movieId = id;
        const movies = data;
        const current = movies.filter(movie => movie._id === movieId)
        console.log(current)
        navigation.navigate('Movie', {
            film: current
        }
        )};

        const postMovie = async () => {
            fetch(`https://codycrudapi.herokuapp.com/api/v1/movies`, {
                method: 'POST',
                'Content-Type': 'application/json',
                body: JSON.stringify({
                  title: title,
                  genre: genre,
                  year: year
                }),
              });
        }
    
    return(
    <SafeAreaView style={styles.container}>
        <Text>Add a Movie to the Archives</Text>
        <TextInput
            placeholder='title'
            onChangeText={onChangeTitle}
            value={title}
        />
        <TextInput
            placeholder='genre'
            onChangeText={onChangeGenre}
            value={genre}
        />
        <TextInput
            placeholder='year'
            onChangeText={onChangeYear}
            value={year}
        />
        <Button title='Submit' onPress={() => postMovie()} />
            
        <Text style={styles.largeHeading}>Archive</Text>
        <FlatList
        value={(item) => item.title}
        id={(item) => item._id}
        keyExtractor={(item) => item._id}
        style={styles.listContainer}
        data={data}
        renderItem={({item}) => (
          <>
            <Pressable  value={item} id={item._id} onPress={() => movieSelect(item._id)}><Text>{item.title}</Text></Pressable>
          </>
        )}
    />
        <Button title='Go to the Home Page' onPress={() => navigation.navigate('Home')} />
    </SafeAreaView>
    );
}
